import React, { Component } from 'react';
import {View, StyleSheet, Platform} from 'react-native';
import MGVideoIOS from './MGVideoIOS'
import MGVideoAndroid from './MGVideoAndroid'


class MGVideo extends Component
{
	render() 
	{
		if (Platform.OS === 'android')
		{
			return (
				<MGVideoAndroid
					style 		= {styles.MGVideoStyle}
	            	videoPath   = {this.props.videoPath}
	            	stopped		= {this.props.needStopPlay}
					resizeMode	= {this.props.resizeMode}/>
			);
		}
		else 
		{
			return (
			<MGVideoIOS 
				style 		= {styles.MGVideoStyle}
				videoPath 	= {this.props.videoPath}/>
			
			);
		}
	}
}

const styles = StyleSheet.create(
{
    MGVideoStyle: {
      position: 'absolute', 
      top: 0, 
      bottom: 0, 
      left: 0, 
      right: 0, 
    },
});

module.exports = MGVideo;