import React from 'react-native';

const {
  requireNativeComponent,
  PropTypes,
  View,
  Component
} = React;

class MGVideoAndroid extends React.Component
{
	render() {
	    return <RCTMGVideo {...this.props} />;
	  }
}

MGVideoAndroid.propTypes = {
    videoPath: PropTypes.string,
    stopped: PropTypes.bool,
    resizeMode: PropTypes.number,
    ...View.propTypes,
};

var RCTMGVideo = requireNativeComponent('RCTMGVideo', MGVideoAndroid, null);

module.exports = MGVideoAndroid;