package com.migu;

import com.facebook.react.bridge.NativeModule;
import com.facebook.react.bridge.ReactApplicationContext;
import com.facebook.react.bridge.ReactContext;
import com.facebook.react.bridge.ReactContextBaseJavaModule;
import com.facebook.react.bridge.ReactMethod;
import com.facebook.react.common.MapBuilder;
import com.facebook.react.bridge.UiThreadUtil;
import com.facebook.react.bridge.Callback;
import android.util.Log;
import android.app.Activity;
import android.content.Context;

public class MGOrientationModule extends ReactContextBaseJavaModule {
    public static final String TAG = "ReactNativeJS";
    public static final String REACT_CLASS = "OrientationModule";
    private Activity mActivity;

    public MGOrientationModule(ReactApplicationContext context) {
        super(context);
    }
    
    public MGOrientationModule(ReactApplicationContext context, Activity activity) {
        super(context);
        mActivity = activity;
    }

    @Override
    public String getName() {
        return REACT_CLASS;
    }
        
    @ReactMethod
    public void setRequestedOrientation(int orientation) {
        Log.i(TAG, "setRequestedOrientation orientation = " + orientation);
        if (mActivity != null) {
            mActivity.setRequestedOrientation(orientation);
        }
    }
}