package com.migu;

import com.facebook.react.bridge.Arguments;
import com.facebook.react.bridge.WritableMap;
import com.facebook.react.uimanager.events.Event;
import com.facebook.react.uimanager.events.RCTEventEmitter;

class MGPlayerPreparedEvent extends Event<MGPlayerPreparedEvent> {

  public static final String EVENT_NAME = "videoPrepared";

  private final int mState;
  
  protected MGPlayerPreparedEvent(int viewTag, long timestampMs, int state) {
    super(viewTag, timestampMs);
    mState = state;
  }

  @Override
  public String getEventName() {
    return EVENT_NAME;
  }

  @Override
  public void dispatch(RCTEventEmitter rctEventEmitter) {
    rctEventEmitter.receiveEvent(getViewTag(), getEventName(), serializeEventData());
  }

  private WritableMap serializeEventData() {
    WritableMap eventData = Arguments.createMap();
    eventData.putInt("state", mState);
    return eventData;
  }
}
